$(document).ready(function(){$.validator.addMethod("enddate",function(e,t){var a=$("#start-time").val(),r=$("#end-time").val(),n=$("#event-date").val(),d=$("#event-end-date").val();return!(Date.parse(n+" "+a)>Date.parse(d+" "+r))}," * "+lang.err_end_before_start),$.validator.addMethod("allday",function(e,t){if(0==$("#event-allday").is(":checked"))return!0;var a=$("#event-date").val(),r=$("#event-end-date").val();return!(Date.parse(a+" 12:00 AM")>Date.parse(r+" 11:59 PM"))}," * "+lang.err_end_before_start),$("#event-form").validate({errorElement:"span",errorClass:"uk-text-danger uk-text-bold",rules:{title:{required:!0},"end-time":{enddate:!0},"event-allday":{allday:!0}},messages:{title:{required:" * "+lang.err_enter_title}},errorPlacement:function(e,t){"end-time"==t.attr("name")||"event-allday"==t.attr("name")?e.insertAfter("#date-errors"):e.insertAfter(t)}})});